public class Lab115 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//While loop
		
		//A- initialisation
		//B- condition
		//C- increment
		
		int i= 1;
		while(i <=10) {
			System.out.println("i");
			i++;
		}
		for (int j = 1 ; j <= 10; j++) {
			System.out.println(j);
		}
	}

}
